 package com.example.viewmake6g;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.view.View;
import android.widget.LinearLayout;
import android.widget.TextView;

import com.example.viewmake6g.model.PostItem;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

 public class MainActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        LinearLayout llScrollParent = findViewById(R.id.layout_view);
        List<PostItem> lists = new ArrayList<>();
        for (int i = 0; i < 15  ; i++) {
            PostItem postItem = new PostItem(true, 124,"iu" + i ,"data:image/jpeg;base64,","포스트했어요");

            lists.add(postItem);
        }

        for (PostItem post : lists){
            View v = View.inflate(this, R.layout.post_item, null);
            TextView tvUserName = v.findViewById(R.id.tv_UserName);
            TextView tvPostText = v.findViewById(R.id.tv_PostText);
            tvUserName.setText(post.getUserName());
            tvPostText.setText(post.getPostText());
            llScrollParent.addView(v);
        }

    }
}
